//Nested array 

var abc = {
  "name": "Peter Parker",
  "heroName": "Spiderman",
  "friends": [
    {"heroName": "Deadpool", "skills": ["Martial artist", "Assassin"]},
    {"heroName": "Hulk", "skills": ["Superhuman Speed", "Superhuman Strength"]},
    {"heroName": "Wolverine", "skills": ["Retractable bone claws", "Superhuman senses"]}
  ]
};

var parsed = JSON.parse(JSON.stringify(abc));

for (var i = 0; i < parsed.friends.length; i++) {
  gs.print('HeroName: ' + parsed.friends[i].heroName);
  gs.print('Skills: ' + parsed.friends[i].skills.join(', '));
}

