var grAgg=new GlideAggregate('incident');
grAgg.addAggregate('count','category');
// grAgg.addQuery('priority',3);
grAgg.groupBy('category');
grAgg.query();
while(grAgg.next())
{
	var countRec=0;
	gs.print('Number : '+ grAgg.number );
	countRec=grAgg.getAggregate('count','category');
	gs.print('Category Name : '+grAgg.getValue('category')+' Count '+ countRec);
}


