sys_script_include.script:
function validateEmailAddress(emailAddress)
{
	emailAddress=emailAddress.toString();//Convert to STring
	if(emailAddress.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/))
  { return true;}
	else
		{
			return false;
		}
}

